actions_1.MenuRegistry.appendMenuItem(actions_1.MenuId.EmptyEditorGroupContext, { command: { id: fileActions_1.GlobalNewUntitledFileAction.ID, title: nls.localize('newFile', "New File") }, group: '1_file', order: 10 });
actions_1.MenuRegistry.appendMenuItem(actions_1.MenuId.EmptyEditorGroupContext, { command: { id: 'workbench.action.quickOpen', title: nls.localize('openFile', "Open File...") }, group: '1_file', order: 20 });
// File menu
actions_1.MenuRegistry.appendMenuItem(actions_1.MenuId.MenubarFileMenu, {
    group: '1_new',
    command: {
        id: fileActions_1.GlobalNewUntitledFileAction.ID,
